var container, panorama, viewer;
 
container = document.querySelector('#container');

// panorama = new PANOLENS.ImagePanorama('/images/panorama.jpg');
panorama = new PANOLENS.ImagePanorama('/images/panorama.jpg');
 
// viewer = new PANOLENS.Viewer({container: container});
viewer = new PANOLENS.Viewer({container: container, output: 'overlay'});
viewer.add(panorama);

// ホットスポットの作成
var infospot1 = new PANOLENS.Infospot(1000, PANOLENS.DataImage.Info);
infospot1.position.set(-2056, -1847, -4156);
infospot1.addHoverText('見る！');

// ホットスポットをパノラマに追加
panorama.add(infospot1);

// ホットスポットをクリックした際の処理
infospot1.addEventListener('click', function() {
    // ページ遷移を行う（例：Googleに遷移）
    window.location.href = 'https://www.google.com';
});

